insert into categorias (nombre, descripcion) values ('Placas Bases', 'Es una tarjeta de circuito impreso a la que se conectan los componentes que constituyen la computadora.');
insert into categorias (nombre, descripcion) values ('Procesadores', 'Es la unidad de procesamiento encargada de interpretar las instrucciones de un hardware haciendo uso de distintas operaciones aritméticas y matemáticas.');
insert into categorias (nombre, descripcion) values ('Memorias RAMs', 'La memoria de acceso aleatorio se utiliza como memoria de trabajo de computadoras y otros dispositivos para el sistema operativo, los programas y la mayor parte del software.');
insert into categorias (nombre, descripcion) values ('Discos Duros', 'Es la unidad de almacenamiento, encargada de guardar la información.');
insert into categorias (nombre, descripcion) values ('Tarjetas Gráficas', 'Está dedicada al procesamiento de datos relacionados con el vídeo y las imágenes que se están reproduciendo en el ordenador.');
insert into categorias (nombre, descripcion) values ('Fuentes de Alimentación', 'Una fuente de alimentación convierte la corriente alterna en una forma continua de energía que los componentes del ordenador necesitan para funcionar, llamada corriente continua.');
insert into categorias (nombre, descripcion) values ('Tarjetas de Red', 'Es un dispositivo introducido en la placa base o a través de usb para que la computadora se sume a una red, y así poder comunicarse entre otras computadoras.');
insert into categorias (nombre, descripcion) values ('Torres', 'Es una especie de estructura donde se alojan los distintos componentes que forman parte de la computadora.');
insert into categorias (nombre, descripcion) values ('Teclados', 'Se utiliza para mandar órdenes al ordenador a trabés de teclas alfanuméricas.');
insert into categorias (nombre, descripcion) values ('Ratones', 'Es un dispositivo señalador con el que mandamos un pulso a un punto en concreto situado en la pantalla.');
insert into categorias (nombre, descripcion) values ('Monitores', 'Muestra datos o información al usuario.');
insert into categorias (nombre, descripcion) values ('Altavoces', 'Reproduce sonidos interpretados por la computadora.');
insert into categorias (nombre, descripcion) values ('Impresoras', 'Ofrece la posibilidad de imprimir sobre papel u otros tipos de sustrato los textos o gráficos producidos por una aplicación.');
